#include<iostream>
using namespace std;int main(){int t;cin>>t;while(t--){string a,b;cin>>a;cin>>b;char n=*(a.end()-1);int m;if(b.length()>=2) m=(*(b.end()-2)-'0')*10+(*(b.end()-1)-'0');else m=*(b.begin())-'0';short int a2[4]={6,2,4,8},a3[4]={1,3,9,7},a4[2]={6,4},a7[4]={1,7,9,3},a8[4]={6,8,4,2},a9[2]={1,9};if(b=="0"){if(a=="0") cout<<"0\n";else cout<<"1\n";}else {switch(n){case'0':cout<<"0\n";break;case'1':cout<<"1\n";break;case'5':cout<<"5\n";break;case'6':cout<<"6\n";break;case'2':cout<<a2[m%4]<<"\n";break;case'3':cout<<a3[m%4]<<endl;break;case'4':cout<<a4[m%2]<<endl;break;case'7':cout<<a7[m%4]<<endl;break;case'8':cout<<a8[m%4]<<endl;break;case'9':cout<<a9[m%2]<<endl;break;}}}return 0;}